//ф-ии регистрации, авторизации и  проверки токена на валидность
import { $host } from './index';

export const registration = async (login, password) => {
    const response = await $host.post('api/user/registration', { login, password })
    return response
}

export const login = async (login, password) => {
    const response = await $host.post('api/user/login', { login, password })
    return response
}

export const check = async () => {
    const response = await $host.post('api/user/check',)
    return response
}
