import React, { useContext } from 'react';
import { Card, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { COURSE_ROUTE } from '../utils/consts';
import { Context } from "../index"

const CourseItem = () => {
    const navigate = useNavigate()
    const { course } = useContext(Context)
    return (
        <Col md={6} onClick={() => navigate(COURSE_ROUTE + '/' + course.course_id)}>
            <Card className="mt-2 p-4" style={{ width: 600 }}>
                {course.types.map(type =>
                    <div key={type.type_id}>{type.type_name}</div>
                )}
                <h2>{course.course_name}</h2>
                <div>close date</div>
                <div>{course.course_duration} часов</div>
            </Card>
        </Col>
    );
};

export default CourseItem;
