import React, { useState } from 'react';
import { Card, Col, Row } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { COURSE_ROUTE } from '../utils/consts';

const CourseItem = ({ course }) => {
    const navigate = useNavigate()
    const [type, setType] = useState({ course_type: '' })
    return (
        <Col md={6} onClick={() => navigate(COURSE_ROUTE + '/' + course.course_id)}>
            <Card className="mt-2 p-4" style={{ width: 600 }}>
                {type.course_type.map(module =>
                    <Row key={module.type_id} >
                        <h3 style={{ paddingLeft: 200 }}>{module.type_name}</h3>
                    </Row>
                )}
                <h2>{course.course_name}</h2>
                <div>close date</div>
                <div>{course.course_duration} часов</div>
            </Card>
        </Col>
    );
};

export default CourseItem;
