import React, { useContext } from 'react';
import { Context } from '../index'
import Container from 'react-bootstrap/Container';
import { NavLink, useNavigate } from 'react-router-dom';
import { COURSES_ROUTE } from '../utils/consts';
import { Button, Navbar, Nav } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';

//observer-для перерендера в режиме реального времени, чтобы mobx мог отслеживать изменения занчений в состоянии
const NavBar = observer(() => {
    const { user } = useContext(Context)
    const navigate = useNavigate()
    return (
        <Navbar bg="dark" variant="dark">
            <Container>
                <NavLink style={{ color: 'white', textDecoration: 'none' }} to={COURSES_ROUTE}>ДВУНЦ</NavLink>
                {user.isAuth ?
                    <Nav className="ml-auto">
                        <Button variant={"outline-light"} onClick={() => navigate(COURSES_ROUTE)}>Выйти</Button>
                    </Nav>
                    :
                    <Nav className="ml-auto">
                        <Button variant={"outline-light"} onClick={() => { user.setIsAuth(true) }}>Войти</Button>
                    </Nav>
                }
            </Container>
        </Navbar >
    );
});

export default NavBar;
