import React, { useContext, useState } from 'react';
import { Modal, Button, Dropdown, Form, Row, Col } from 'react-bootstrap';
import { Context } from "../../index";

const CreateCourse = ({ show, onHide }) => {
    const { course } = useContext(Context)
    const [module, setModule] = useState([])
    const [name, setName] = useState([])
    const [duration, setDuration] = useState([])
    const [price, setPrice] = useState([])
    const addModule = () => {
        setModule([...module, { module_id: Date.now(), module_name: '' }])
    }
    const removeModule = (module_id) => {
        setModule(module.filter(i => i.module_id !== module_id))
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить курс
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Dropdown>
                    <Dropdown.Toggle>
                        Выберите тип
                        <Dropdown.Menu>
                            {course.types.map(type =>
                                <Dropdown.Item key={type.type_id}>{type.type_name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown.Toggle>
                </Dropdown>
                <Dropdown className="mt-2">
                    <Dropdown.Toggle>
                        Выберите формат занятий
                        <Dropdown.Menu>
                            {course.formats.map(format =>
                                <Dropdown.Item key={format.format_id}>{format.format_name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown.Toggle>
                </Dropdown>
                <Form.Control
                    className="mt-2"
                    placeholder="Введите название "
                />
                <Form.Control
                    className="mt-2"
                    placeholder="Введите длительность"
                    type="number"
                />
                <Form.Control
                    className="mt-2"
                    placeholder="Введите стоимость"
                    type="number"
                />
                <hr />
                <Button onClick={addModule} variant="outline-dark">Добавить новый модуль</Button>
                {
                    module.map(i =>
                        <Row className="mt-2" key={i.module_id}>
                            <Col md={4}>
                                <Form.Control
                                    placeholder="Введите название"
                                />
                            </Col>
                            <Col>
                                <Button onClick={() => removeModule(i.module_id)} variant={'outline-danger'}>
                                    Удалить
                                </Button>
                            </Col>
                        </Row>
                    )
                }
                <hr />
                <Button onClick={addModule} variant="outline-dark">Добавить расписание</Button>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateCourse;
